  &__guarantee {
    position: absolute;
    width: 226px;
    height: 37px;
    background-color: white;
    color: black;
    text-transform: uppercase;
    font: {
      weight: 500;
      size: 15px;
    }
    line-height: 18px;
    display: flex;
    align-items: center;
    justify-content: center;
    transform: rotate(-15deg);
    z-index: 5;
    bottom: -3px;
    left: 10px;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 7px;
    border: 1px solid black;
  }

  &__container {
    height: 755px;
    width: 783px;
    margin: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    overflow-y: hidden;
    position: relative;

    &:before {
      content: '';
      position: absolute;
      width: 783px;
      height: 783px;
      border-radius: 392px;
      background-color: #FED23F;
      z-index: 0;
    }
  }

  &__title {
    margin-top: 150px;
    margin-left: -15%;
    z-index: 2;
    color: black;
    font: {
      size: 32px;
      weight: 700;
      family: 'Montserrat';
    }
    line-height: 28px;
    text-transform: uppercase;
    transform: rotate(-10deg);
  }

  &__pictures {
    position: relative;
    margin-top: 80px;
    width: 638px;
    height: 611px;
  }

  &__pic {
    position: absolute;
    width: 235px;
    height: 418px;

    &_1 {
      width: 228px;
      height: 399px;
      top: 0px;
      left: 25%;
      transform: rotate(-10.61deg);
    }

    &_2 {
      width: 235px;
      height: 418px;
      top: 9px;
      left: 42%;
      transform: rotate(19.77deg);
    }
  }

  &__button {
    position: absolute;
    width: fit-content;
    height: fit-content;
    background: transparent;
    right: 0;
    bottom: 176px
  }

  &__shape {
    width: 228px;
    height: 163px;

    & h3 {
      margin-top: 25px
    }
  }

  &__footer {
    height: 80px;
    padding-top: 34px;
    width: 100%;
    display: flex;
    align-items: flex-end;
    justify-content: space-between;
  }

  &__menu {
    color: white!important;
  }

  &__logo {
    color: white;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    font-family: 'Roboto', sans-serif;
    height: 100%;

    & h3 {
      font: {
        weight: 400;
        size: 16px;
      }
      line-height: 19px;
      text-transform: uppercase;
    }
  }

  &-mark {
    display: flex;
    justify-content: space-between;
    width: 180px;
    position: relative;

    &:after {
      content: '';
      position: absolute;
      left: 49%;
      height: 100%;
      width: 1px;
      background-color: white;
    }

    & p {
      font: {
        weight: 300;
        size: 8px;
      }
      line-height: 9px;
    }
  }
}
