  &__container {
    position: absolute;
    width: 751px;
    height: 103px;
    top: 200px;
    left: 55px;
    transform: rotate(-15deg);
    user-select: text;

    @media #{$mobile} {
      width: 100%;
      height: fit-content;
      display: flex;
      flex-direction: column;
      justify-content: space-around;
      align-items: center;
      top: 120px;
      left: 22px;
    }

    &-lower {
      display: flex;
      align-items: center;

      @media #{$mobile} {
        width: 296px;
        justify-content: space-between;
      }
    }
  }

  &__title {
    margin-bottom: 0;
    font: {
      family: 'Montserrat', sans-serif;
      size: 45px;
      weight: 700;
    }
    line-height: 55px;
    letter-spacing: 0.005em;
    text-transform: uppercase;

    @media #{$mobile} {
      font-size: 18px;
      line-height: 20px;
    }

    &--upper {
      @media #{$mobile} {
        width: 296px;
      }
    }

    &--pre {
      font-size: 17px;
      line-height: 21px;
      transform: rotate(90deg);

      @media #{$mobile} {
        width: 9px;
        font-size: 5px;
        line-height: 6px !important;
      }
    }

    &--main {
      font-size: 55px;
      line-height: 45px;

      @media #{$mobile} {
        font-size: 18px;
        line-height: 20px;
      }
    }
  }

  &__subtitle {
    margin-top: 12px;
    font: {
      weight: 300;
      size: 14px;
    }
    line-height: 16px;
  }

  &__slang {
    background-color: #020000;
    color: #FFFFFF;
    width: 347px;
    border-radius: 7px;
    height: 35px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-transform: uppercase;
    line-height: 18px;
    font: {
      weight: 500;
      size: 15px
    }

    @media #{$mobile} {
      width: 162px;
      height: 12px;
      font-size: 5px;
      line-height: 6px;
      border-radius: 2px;
      background-color: rgba(2, 0, 0, 0.8);
    }
  }

  &__mobile-container {

    @media #{$mobile} {
      margin-top: 5px;
      display: flex;
      align-items: center;
      justify-content: center;
      width: 100%;
      height: 224px;
      position: relative;

    }

    &:before {
      position: absolute;
      content: '';
      background-color: #FBB900;
      left: -30%;
      width: 140%;
      height: 224px;
    }
  }

  &__mobile-pictures {
    width: 299px;
    height: inherit;
    transform: rotate(15deg);
    position: relative;
  }

  &__content-mobile {
    object-fit: cover;
    position: absolute;
    top: 60px;
    left: 20px;
    width: 80px;
    height: 145px;
    transform: rotate(-19deg);
    filter: drop-shadow(0px 2px 4px #987510);
  }

  &__cover-mobile {
    object-fit: cover;
    position: absolute;
    top: 38px;
    right: 157px;
    width: 74px;
    height: 136px;
    transform: rotate(6.21deg);
    filter: drop-shadow(0px 4px 2px #987510);
  }

  &__iphone-mobile {
    object-fit: cover;
    position: absolute;
    top: 20px;
    right: 85px;
    width: 100px;
    height: 200px;
    transform: rotate(26.21deg);
  }

  &__phone {
    position: absolute;
    top: 100px;
    right: 55px;
    width: 291px;
    height: 516px;
    transform: rotate(26.21deg);
  }

  &__iphone {
    position: relative;
    width: inherit;
    height: inherit;

    @media #{$mobile} {
      display: none;
    }

    & img {
      position: absolute;
      right: 29px;
      bottom: 0;
      width: inherit;
      height: inherit;
      z-index: 0
    }
  }

  &__buy {
    font-size: 21px;
    line-height: 25px;
    margin-top: 18px;
    color: #fff9f9;
  }
}

